import React, {useEffect, useState} from 'react';
import '../index.css';
import {Link, useLocation, useNavigate} from "react-router-dom";
//import Races from "./Races";
import axios from 'axios';


function UserHome() {

    const {state} = useLocation();
    const {id, username, password, role} = state;

    const [packages, setPackages] = useState()
    const [race, setRace] = useState()

    const [devices, setDevices] = useState()

    const navigate = useNavigate()

    const totalprice = 0

    const addToCart = async (f) => {
        const helper = f + ''
        axios.post('http://localhost:8081/user/addToCart/'+ f.id).then(response => {
            totalprice += f.price
            alert("Total price currently is: " + totalprice.toString())
        }
        )
    }

    const getDevices = async e => {
        e.preventDefault();
        axios.get('http://localhost:8080/clients/getDevices' + id).then(
            response => setDevices(response.data)
        )
    }

    const placeOrder = async () => {
        axios.post('http://localhost:8081/user/order/'+ id).then(
            alert("Order succesfully placed!")
        )
    }

    return (
        <div className='App'>
            <div>
               <Races setRace={setRace} setPackages={setPackages}/>
            </div>

            <div>
                <button onClick={getDevices}> Devices </button>
                <h1> Devices </h1>
                {
                devices?.map((g, i) => (
                    <div key={i}>
                            <h2> {g.id}: {g.description} </h2>
                            <p> {g.address} </p>
                            <p> {g.maxConsHr} </p>
                    </div>
                ))}
            </div>

            <div>
                Here I need to put consumptions
            </div>
        </div>)
}

export default UserHome;
