import axios from "axios";
import React, {useEffect, useState} from "react";
import Calendar from "react-calendar";

function Consumption({setRace, setConsumption}) {
    const [name, setname] = useState()
    const [races, setRaces] = useState()
    const [date, setDate] = useState()
    const [devID, setDevID] = useState()

    useEffect(() => {
        const getRaces = async () => {
            axios.get('http://localhost:8081/user/getRaces').then(
                response => setRaces(response.data)
            )
        }
        getRaces();
    }, [])

    const getConsumption = async(date) => {
        axios.post('http://localhost:8081/clients', {
            devID:devID,
            date:date
        }).then (
            response => {
                alert(date)
                setConsumption(response.data)
            }
        )
    }

    return (
        <div>
            <h1> Consumption </h1>
                <h2>Please select a date to show consumption!</h2>
                <div>
                    <Calendar onChange={setDate} value={date} />
                </div>
                {
                }
        </div>
    )
}

export default Consumption;
