import axios from "axios";
import React, {useEffect, useState} from "react";
import * as SockJS from "sockjs-client";
import * as Stomp from "stompjs";
import './User.css';

var stompClient = null;

function setConnected(connected) {
  document.getElementById("connect").disabled = connected;
  document.getElementById("disconnect").disabled = !connected;
  document.getElementById("conversationDiv").style.visibility = connected
    ? "visible"
    : "hidden";
  document.getElementById("response").innerHTML = "";
}

function connect() {
  const user = localStorage.getItem("user");
  var socket = new SockJS("http://127.0.0.1:8080/ws");
  stompClient = Stomp.over(socket);
  if (user) {
    stompClient.connect({}, function(frame) {
      setConnected(true);
      console.log("Connected: " + frame);
      stompClient.subscribe("/all/messages/" + user, function(
        messageOutput
      ) {
        showMessageOutput(JSON.parse(messageOutput.body));
      });
    });
  }
}

function disconnect() {
  console.log("here");
  if (stompClient != null) {
    stompClient.disconnect();
    stompClient = null;
  }
  setConnected(false);
  console.log("Disconnected");
}

function showMessageOutput(messageOutput) {
  console.log(messageOutput);
  var response = document.getElementById("response");
  var p = document.createElement("p");
  p.style.wordWrap = "break-word";
  p.appendChild(
    document.createTextNode(
      new Date(messageOutput.time).toDateString() +
        " " +
        "Dear user called " + 
        ": " +
        messageOutput.text
    )
  );
  response.appendChild(p);
}

function WebSk() {



    return ( 
        <div>
            <div>
                <div>
                <button id="connect" onClick={connect}>
                    Connect
                </button>
                <button id="disconnect" onClick={() => {disconnect();}}>
                    Disconnect
                </button>
                </div>
                <br />
                <div id="conversationDiv">
                <p id="response"></p>
                </div>
            </div>

            <label>Insert something here</label>
            <br></br>
            <input type='text' id="idk"></input>
        </div>
    )
}

export default WebSk;
