import axios from "axios";
import React, {useEffect, useState} from "react";
import Calendar from "react-calendar";
import './User.css'

function Consumption() {
    const [name, setname] = useState()
    const [consumption, setConsumption] = useState()
    const [date, setDate] = useState(new Date())
    const [devID, setDevID] = useState()
/*
    useEffect(() => {
        const getRaces = async () => {
            axios.get('http://localhost:8081/user/getRaces').then(
                response => setRaces(response.data)
            )
        }
        getRaces();
    }, [])
*/
    const getConsumption = async e => {
        e.preventDefault()
        axios.post('http://localhost:8081/clients/getConsumption', {
            devID:devID,
            date:date
        }).then (
            response => {
                alert(date)
                setConsumption(response.data)
            }
        ).catch((error) => {
            console.log(error.text);
            alert(error)
        })
    }

    return (
        <div class='consumption'>
            <h1> Consumption </h1>
            <label>Device ID</label>
            <br></br>
            <br></br>
            <input type='text' onChange={setDevID}></input>
            <br></br>
            <br></br>
            <label>Consumption Date</label>
            <br></br>
            <br></br>
            <div class='calendar'>
                <Calendar onChange={date => setDate(date)}/>
            </div>
            <br></br>
            <button onClick={getConsumption}>Get Device Consumption</button>
        </div>
    )
}

export default Consumption;
