import axios from "axios";
import React, {useEffect, useState} from "react";

function Consumption({setRace, setPackages}) {
    const [name, setname] = useState()
    const [races, setRaces] = useState()

    useEffect(() => {
        const getRaces = async () => {
            axios.get('http://localhost:8081/user/getRaces').then(
                response => setRaces(response.data)
            )
        }
        getRaces();
    }, [])

    const getPackages = async (id) => {
        var foo = id + ''
        axios.get('http://localhost:8081/user/PackByRest/' + foo).then (
            response => {
                alert(id)
                setPackages(response.data)
                setRace(id)
            }
        )
    }

    return (
        <div>
            <h1> Races </h1>
            {
                races?.map((r, i) => (
                    <div key={i}>
                        <button onClick={() => getPackages(r.id)}> {r.name} </button>
                        <p> Race location: {r.location} </p>
                    </div>
                ))
            }
        </div>
    )
}

export default Consumption;
